import React, {Component} from "react";
import { isPropertySignature } from "typescript";
import ListDisplay from "./ListDisplay";
import './styles/stylecomponent.css';
interface Props{}
interface State{
    task: string;
    tasks: string[];
    showStats: boolean;
}


class TodoList extends Component<Props, State>{

    constructor(props: Props){
        super(props);
        this.state = {
            task: "",
            tasks:[],
            showStats: false
        };
    }

    handleclickadd(e: any){
        e.preventDefault();
        this.setState({
            task: this.state.task,
            tasks: [...this.state.tasks, this.state.task],
            showStats: true
        });
        //console.log(this.state.tasks);
    }

    render(){
        return(
            <div>
                <form className = "form-display">
                    <h2>My TodoList</h2>
                    <input className = "input-display" type = "text" placeholder="Enter task"
                    onChange = {event => this.setState({task: event.target.value})}/>
                    <button className = "btn-display" onClick={this.handleclickadd.bind(this)}>Add Task</button>
                </form>
                {
                    this.state.showStats?
                    <div>
                        <ul>
                        <li>{this.state.tasks.map((todo) => 
                        <div className="list">{todo}<button className="btn-display">Remove Task</button></div>)}
                        </li>
                        </ul>
                    </div>
                    :
                    <div>
                    </div>
                }
            </div>
        );
    }
}

export default TodoList;
